import React, { useState } from "react";
import { attributes } from "../content/home.md";

export default function Skills() {
  const [filter, setFilter] = useState("all");
  let { skills } = attributes;
  let filteredSkills = skills.filter((skill) => {
    console.log(skill.description, filter, skill.description === filter);
    if (filter === "all") {
      return true;
    } else if (skill.description === filter) {
      return true;
    }

    return false;
  });
  console.log(skills);
  return (
    <div className="skills">
      <h1 className="skills_header">Skills</h1>
      <div className="skills_filter">
        <div
          className="skills_filter-tab active"
          onClick={() => setFilter("all")}
        >
          All
        </div>
        <div className="skills_filter-tab" onClick={() => setFilter("front")}>
          Front-End
        </div>
        <div className="skills_filter-tab" onClick={() => setFilter("back")}>
          Back-End
        </div>
        <div className="skills_filter-tab" onClick={() => setFilter("other")}>
          Other
        </div>
      </div>
      <div className="skills_section">
        {filteredSkills.map((skill, key) => {
          return (
            <div className="skills_pill" key={key}>
              {skill.name}
            </div>
          );
        })}
      </div>
    </div>
  );
}
